function tweetIt(txt) {
    var tweet = {
      status: txt
    }

    T.post('statuses/update', tweet, tweeted);

    function tweeted(err, data, response) {
      if (err) {
        console.log("Something went wrong!", err);
      } else {
        console.log("It worked!");
      }
    }
}